BEGIN TRANSACTION;

DROP TABLE IF EXISTS red_wines
DROP TABLE IF EXISTS white_wines

CREATE TABLE red_wines (
    wine_id serial,
    varietal varchar(30) NOT NULL,
    country varchar(20) NOT NULL,
    state_or_region varchar(30) NOT NULL,
    sub_region varchar(30),
    color varchar(20),
    tannin varchar (20),
    alcohol varchar (20),
    body varchar (20),
    acidity varchar(20),
    sweetness varchar (20),
    has_oak boolean NOT NULL,
    fruit_condition varchar(20),
    fruit_type varchar(200),
    nonfruit_notes varchar(200),
    climate varchar (20),
    old_new_world char(2)
);

CREATE TABLE white_wines (
    wine_id serial,
    varietal varchar(30) NOT NULL,
    country varchar(20) NOT NULL,
    state_or_region varchar(30) NOT NULL,
    sub_region varchar(30),
    color varchar(20),
    alcohal varchar (20),
    body varchar (20),
    acidity varchar(20);
    sweetness varchar (20),
    has_oak boolean NOT NULL,
    fruit_condition varchar(20),
    fruit_type varchar(200),
    nonfruit_notes varchar(200),
    climate varchar (20),
    old_new_world char(2)
);



COMMIT;

